mapper.select:
select d.id, d.name, d.category_id, d.price, d.image, d.description, d.status, d.update_time, c.name as category_name, df.id as dish_flavor_id, df.dish_id, df.name as dish_flavor_name, df.value from dish d join category c on d.category_id = c.id left join dish_flavor df on d.id = df.dish_id
and d.name like concat('%', #{name}, '%')
and d.category_id = #{categoryId}
and d.status = #{status}
order by d.create_time desc

mapper.update:
update dish
update_time = #{updateTime}, update_user = #{updateUser}, name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status},
where id = #{id}
